mapper.insert:
INSERT INTO bake.agent_skill_mapping(
	  user_id
	, skill_id
	, skill_level
	, create_date)
VALUES

(
	  #{item.userId}
	, #{item.skillId}
	, #{item.skillLevel}
	, TO_CHAR(CURRENT_TIMESTAMP , 'YYYYMMDDHH24MISS')
)

mapper.delete:
DELETE FROM bake.agent_skill_mapping
WHERE user_id IN
	(
		#{item.userId}
	)
