mapper.select:
SELECT a.id , a.application_id , a.name , a.value , a.group_id , a.created_date , a.created_by , a.modified_date , a.modified_by FROM bake.scheduler_option a , bake.scheduler_option_group b WHERE a.group_id = b.id
a.group_id = #{group_id}
ORDER BY b.name, a.name

mapper.update:
UPDATE bake.scheduler_option_group SET
	  name = #{name}
	, modified_date = to_char(current_timestamp , 'YYYYMMDDHH24MISS')
	, modified_by = 'system'
WHERE 
	  id = #{id}

UPDATE bake.scheduler_option 
SET
	  application_id = #{application_id}
	, name = #{name}
	, value = #{value}
	, group_id = #{group_id}
	, modified_date = to_char(current_timestamp , 'YYYYMMDDHH24MISS')
	, modified_by = 'system'
WHERE 
	  id = #{id}
